import React from 'react';
import {
  Box,
  Typography,
  Card,
  CardContent,
  CardMedia,
  Grid,
  Chip,
} from '@mui/material';

const Education: React.FC = () => {
  const educationItems = [
    {
      title: 'PhD in Computer Science',
      institution: 'University of Example',
      period: '2018 - 2022',
      description: 'Research focused on artificial intelligence and machine learning. Published multiple papers in top-tier conferences.',
      image: '/path-to-university-image.jpg',
      location: 'City, Country',
      tags: ['AI', 'Machine Learning', 'Research'],
    },
    {
      title: 'Master of Science in Computer Science',
      institution: 'Another University',
      period: '2016 - 2018',
      description: 'Specialized in software engineering and distributed systems. Graduated with honors.',
      image: '/path-to-university-image.jpg',
      location: 'City, Country',
      tags: ['Software Engineering', 'Distributed Systems'],
    },
    {
      title: 'Bachelor of Science in Computer Science',
      institution: 'Third University',
      period: '2012 - 2016',
      description: 'Focused on fundamental computer science concepts and practical programming skills.',
      image: '/path-to-university-image.jpg',
      location: 'City, Country',
      tags: ['Computer Science', 'Programming'],
    },
  ];

  return (
    <Box>
      <Typography variant="h4" component="h1" gutterBottom>
        Education
      </Typography>
      <Grid container spacing={3}>
        {educationItems.map((item, index) => (
          <Grid item xs={12} key={index} component="div">
            <Card 
              sx={{ 
                display: 'flex',
                flexDirection: { xs: 'column', md: 'row' },
                height: '100%',
                transition: 'transform 0.2s, box-shadow 0.2s',
                '&:hover': {
                  transform: 'translateY(-4px)',
                  boxShadow: '0 8px 16px rgba(0,0,0,0.1)',
                },
              }}
            >
              <CardMedia
                component="img"
                sx={{
                  width: { xs: '100%', md: 240 },
                  height: { xs: 200, md: 'auto' },
                  objectFit: 'cover',
                }}
                image={item.image}
                alt={`${item.institution} campus`}
              />
              <Box sx={{ display: 'flex', flexDirection: 'column', flexGrow: 1 }}>
                <CardContent sx={{ flex: '1 0 auto' }}>
                  <Typography variant="h5" component="div" gutterBottom>
                    {item.title}
                  </Typography>
                  <Typography variant="h6" color="primary" gutterBottom>
                    {item.institution}
                  </Typography>
                  <Typography variant="subtitle1" color="text.secondary" gutterBottom>
                    {item.period} â€¢ {item.location}
                  </Typography>
                  <Typography variant="body1" paragraph>
                    {item.description}
                  </Typography>
                  <Box sx={{ display: 'flex', gap: 1, flexWrap: 'wrap' }}>
                    {item.tags.map((tag, tagIndex) => (
                      <Chip
                        key={tagIndex}
                        label={tag}
                        size="small"
                        sx={{
                          backgroundColor: 'rgba(33, 150, 243, 0.1)',
                          color: '#2196F3',
                          '&:hover': {
                            backgroundColor: 'rgba(33, 150, 243, 0.2)',
                          },
                        }}
                      />
                    ))}
                  </Box>
                </CardContent>
              </Box>
            </Card>
          </Grid>
        ))}
      </Grid>
    </Box>
  );
};

export default Education;
