import React from 'react';
import {
  Box,
  Typography,
  Card,
  CardContent,
  CardMedia,
  Chip,
  Link,
} from '@mui/material';
import GitHubIcon from '@mui/icons-material/GitHub';
import LinkedInIcon from '@mui/icons-material/LinkedIn';
import TwitterIcon from '@mui/icons-material/Twitter';
import EmailIcon from '@mui/icons-material/Email';

const About: React.FC = () => {
  const themeColor = '#607D8B'; // Blue Grey

  const socialLinks = [
    {
      icon: <GitHubIcon />,
      label: 'GitHub',
      url: 'https://github.com/yourusername',
    },
    {
      icon: <LinkedInIcon />,
      label: 'LinkedIn',
      url: 'https://linkedin.com/in/yourusername',
    },
    {
      icon: <TwitterIcon />,
      label: 'Twitter',
      url: 'https://twitter.com/yourusername',
    },
    {
      icon: <EmailIcon />,
      label: 'Email',
      url: 'mailto:your.email@example.com',
    },
  ];

  const interests = [
    'Machine Learning',
    'Artificial Intelligence',
    'Computer Vision',
    'Natural Language Processing',
    'Deep Learning',
    'Software Engineering',
  ];

  return (
    <Box>
      <Typography 
        variant="h4" 
        gutterBottom 
        sx={{ 
          fontWeight: 600,
          mb: 4,
        }}
      >
        About Me
      </Typography>
      <Card sx={{ mb: 4 }}>
        <CardContent>
          <Typography variant="body1" paragraph>
            [Your detailed biography and background information. This could include your academic journey, research interests, professional experience, and personal interests.]
          </Typography>
          <Box sx={{ display: 'flex', gap: 1, flexWrap: 'wrap' }}>
            {interests.map((interest, index) => (
              <Chip 
                key={index} 
                label={interest} 
                size="small"
                sx={{ 
                  backgroundColor: `${themeColor}15`,
                  color: themeColor,
                  '&:hover': {
                    backgroundColor: `${themeColor}25`,
                  },
                }}
              />
            ))}
          </Box>
        </CardContent>
      </Card>
      <Typography variant="h5" gutterBottom sx={{ fontWeight: 600, mb: 3 }}>
        Contact & Social
      </Typography>
      <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2 }}>
        {socialLinks.map((link, index) => (
          <Link 
            key={index}
            href={link.url} 
            target="_blank" 
            rel="noopener noreferrer"
            sx={{ 
              color: themeColor,
              textDecoration: 'none',
              display: 'flex',
              alignItems: 'center',
              gap: 1,
              '&:hover': {
                textDecoration: 'underline',
              },
            }}
          >
            {link.icon}
            {link.label}
          </Link>
        ))}
      </Box>
    </Box>
  );
};

export default About;
