import React from 'react';
import { 
  Typography, 
  Box, 
  Grid, 
  Card, 
  CardContent, 
  CardMedia,
  Link,
  Chip,
} from '@mui/material';
import { useTheme } from '@mui/material/styles';

const Home: React.FC = () => {
  const theme = useTheme();

  const newsItems = [
    {
      title: 'New Research Publication',
      date: 'March 15, 2024',
      description: 'Our latest research on sustainable urban development has been published in the Journal of Urban Studies.',
      image: '/path-to-news-image1.jpg',
      link: 'https://example.com/publication',
      tags: ['Research', 'Publication'],
    },
    {
      title: 'Conference Presentation',
      date: 'February 28, 2024',
      description: 'Presented our work on community engagement at the International Conference on Urban Development.',
      image: '/path-to-news-image2.jpg',
      link: 'https://example.com/conference',
      tags: ['Conference', 'Presentation'],
    },
    {
      title: 'Teaching Award',
      date: 'January 20, 2024',
      description: 'Received the Excellence in Teaching Award for innovative approaches to urban planning education.',
      image: '/path-to-news-image3.jpg',
      link: 'https://example.com/award',
      tags: ['Award', 'Teaching'],
    },
  ];

  return (
    <Box>
      <Typography variant="h4" gutterBottom sx={{ fontWeight: 600, mb: 4 }}>
        Welcome to My Personal Website
      </Typography>

      <Typography variant="h5" gutterBottom sx={{ fontWeight: 600, mb: 3, color: theme.palette.primary.main }}>
        Latest News
      </Typography>

      <Grid container spacing={3}>
        {newsItems.map((item, index) => (
          <Box key={index} sx={{ width: '100%' }}>
            <Card 
              sx={{ 
                display: 'flex',
                flexDirection: { xs: 'column', sm: 'row' },
                transition: 'transform 0.2s, box-shadow 0.2s',
                '&:hover': {
                  transform: 'translateY(-4px)',
                  boxShadow: theme.shadows[4],
                },
              }}
            >
              <CardMedia
                component="img"
                sx={{
                  width: { xs: '100%', sm: 240 },
                  height: { xs: 200, sm: 'auto' },
                  objectFit: 'cover',
                }}
                image={item.image}
                alt={item.title}
              />
              <CardContent sx={{ flex: 1, display: 'flex', flexDirection: 'column' }}>
                <Box sx={{ mb: 2 }}>
                  {item.tags.map((tag, tagIndex) => (
                    <Chip
                      key={tagIndex}
                      label={tag}
                      size="small"
                      sx={{ 
                        mr: 1, 
                        mb: 1,
                        backgroundColor: `${theme.palette.primary.main}15`,
                        color: theme.palette.primary.main,
                        '&:hover': {
                          backgroundColor: `${theme.palette.primary.main}25`,
                        },
                      }}
                    />
                  ))}
                </Box>
                <Typography variant="h6" gutterBottom sx={{ fontWeight: 600 }}>
                  {item.title}
                </Typography>
                <Typography variant="subtitle2" color="text.secondary" gutterBottom>
                  {item.date}
                </Typography>
                <Typography variant="body1" paragraph>
                  {item.description}
                </Typography>
                <Box sx={{ mt: 'auto' }}>
                  <Link 
                    href={item.link} 
                    target="_blank" 
                    rel="noopener noreferrer"
                    sx={{ 
                      color: theme.palette.primary.main,
                      textDecoration: 'none',
                      '&:hover': {
                        textDecoration: 'underline',
                      },
                    }}
                  >
                    Read More â†’
                  </Link>
                </Box>
              </CardContent>
            </Card>
          </Box>
        ))}
      </Grid>
    </Box>
  );
};

export default Home;
