import React from 'react';
import {
  Box,
  Typography,
  Card,
  CardContent,
  Chip,
  Link,
  CardMedia,
} from '@mui/material';
import OpenInNewIcon from '@mui/icons-material/OpenInNew';

const Publications: React.FC = () => {
  const publicationItems = [
    {
      title: 'Machine Learning Approaches for Natural Language Processing',
      authors: 'Smith, J., Johnson, A., & Your Name',
      journal: 'Journal of Artificial Intelligence Research',
      year: '2023',
      description: 'This paper presents novel approaches to natural language processing using advanced machine learning techniques.',
      doi: '10.1234/example.2023',
      tags: ['Machine Learning', 'NLP', 'AI'],
      link: 'https://example.com/paper1',
    },
    {
      title: 'Deep Learning in Computer Vision: A Comprehensive Review',
      authors: 'Your Name, Brown, M., & Davis, R.',
      journal: 'IEEE Transactions on Pattern Analysis',
      year: '2022',
      description: 'A comprehensive review of recent advances in deep learning applications for computer vision tasks.',
      doi: '10.1234/example.2022',
      tags: ['Deep Learning', 'Computer Vision', 'Review'],
      link: 'https://example.com/paper2',
    },
    {
      title: 'Optimizing Neural Network Architectures for Edge Devices',
      authors: 'Wilson, P., Your Name, & Thompson, K.',
      journal: 'ACM Computing Surveys',
      year: '2021',
      description: 'Research on efficient neural network architectures suitable for deployment on resource-constrained edge devices.',
      doi: '10.1234/example.2021',
      tags: ['Neural Networks', 'Edge Computing', 'Optimization'],
      link: 'https://example.com/paper3',
    },
  ];

  const themeColor = '#9C27B0'; // Purple

  return (
    <Box>
      <Typography 
        variant="h4" 
        gutterBottom 
        sx={{ 
          fontWeight: 600,
          mb: 4,
        }}
      >
        Publications
      </Typography>
      <Box sx={{ display: 'flex', flexDirection: 'column', gap: 3 }}>
        {publicationItems.map((item, index) => (
          <Card 
            key={index}
            sx={{ 
              display: 'flex',
              flexDirection: { xs: 'column', md: 'row' },
              transition: 'transform 0.2s, box-shadow 0.2s',
              '&:hover': {
                transform: 'translateY(-4px)',
                boxShadow: '0 8px 16px rgba(0,0,0,0.1)',
              },
            }}
          >
            <CardMedia
              component="img"
              sx={{ 
                width: { xs: '100%', md: 200 },
                height: { xs: 200, md: 'auto' },
                objectFit: 'cover',
              }}
              image={item.image}
              alt={item.title}
            />
            <CardContent sx={{ flex: 1 }}>
              <Typography variant="h5" component="div" gutterBottom sx={{ fontWeight: 600 }}>
                {item.title}
              </Typography>
              <Typography variant="subtitle1" color="text.secondary" gutterBottom>
                {item.authors}
              </Typography>
              <Typography variant="body2" color="text.secondary" gutterBottom>
                {item.journal} • {item.year}
              </Typography>
              <Typography variant="body1" paragraph>
                {item.description}
              </Typography>
              <Box sx={{ display: 'flex', gap: 1, flexWrap: 'wrap', mb: 2 }}>
                {item.tags.map((tag, tagIndex) => (
                  <Chip 
                    key={tagIndex} 
                    label={tag} 
                    size="small"
                    sx={{ 
                      backgroundColor: `${themeColor}15`,
                      color: themeColor,
                      '&:hover': {
                        backgroundColor: `${themeColor}25`,
                      },
                    }}
                  />
                ))}
              </Box>
              <Box sx={{ display: 'flex', gap: 2, alignItems: 'center' }}>
                <Link 
                  href={item.link} 
                  target="_blank" 
                  rel="noopener noreferrer"
                  sx={{ 
                    color: themeColor,
                    textDecoration: 'none',
                    display: 'flex',
                    alignItems: 'center',
                    gap: 0.5,
                    '&:hover': {
                      textDecoration: 'underline',
                    },
                  }}
                >
                  Read Paper
                  <OpenInNewIcon sx={{ fontSize: 16 }} />
                </Link>
                <Typography variant="body2" color="text.secondary">
                  DOI: {item.doi}
                </Typography>
              </Box>
            </CardContent>
          </Card>
        ))}
      </Box>
    </Box>
  );
};

export default Publications;
