import React from 'react';
import {
  Box,
  Typography,
  Card,
  CardContent,
  Chip,
  Link,
} from '@mui/material';
import SchoolIcon from '@mui/icons-material/School';
import PeopleIcon from '@mui/icons-material/People';
import CalendarTodayIcon from '@mui/icons-material/CalendarToday';

const Teaching: React.FC = () => {
  const teachingItems = [
    {
      title: 'Advanced Machine Learning',
      courseCode: 'CS501',
      semester: 'Fall 2023',
      description: 'Graduate-level course covering advanced topics in machine learning, including deep learning, reinforcement learning, and natural language processing.',
      students: '45',
      tags: ['Machine Learning', 'Deep Learning', 'Graduate'],
      syllabus: 'https://example.com/syllabus1',
    },
    {
      title: 'Introduction to Artificial Intelligence',
      courseCode: 'CS301',
      semester: 'Spring 2023',
      description: 'Undergraduate course introducing fundamental concepts in artificial intelligence, including search algorithms, knowledge representation, and basic machine learning.',
      students: '120',
      tags: ['AI', 'Undergraduate', 'Foundations'],
      syllabus: 'https://example.com/syllabus2',
    },
    {
      title: 'Data Structures and Algorithms',
      courseCode: 'CS201',
      semester: 'Fall 2022',
      description: 'Core computer science course covering fundamental data structures and algorithms, with emphasis on practical implementation and analysis.',
      students: '85',
      tags: ['Algorithms', 'Data Structures', 'Core'],
      syllabus: 'https://example.com/syllabus3',
    },
  ];

  return (
    <Box>
      <Typography variant="h4" component="h1" gutterBottom>
        Teaching
      </Typography>
      <Box sx={{ display: 'flex', flexDirection: 'column', gap: 3 }}>
        {teachingItems.map((item, index) => (
          <Card 
            key={index}
            sx={{ 
              transition: 'transform 0.2s, box-shadow 0.2s',
              '&:hover': {
                transform: 'translateY(-4px)',
                boxShadow: '0 8px 16px rgba(0,0,0,0.1)',
              },
            }}
          >
            <CardContent>
              <Typography variant="h5" component="div" gutterBottom>
                {item.title}
              </Typography>
              <Typography variant="h6" color="primary" gutterBottom>
                {item.courseCode}
              </Typography>
              <Box sx={{ display: 'flex', gap: 2, mb: 2 }}>
                <Box sx={{ display: 'flex', alignItems: 'center', gap: 0.5 }}>
                  <CalendarTodayIcon fontSize="small" color="action" />
                  <Typography variant="body2" color="text.secondary">
                    {item.semester}
                  </Typography>
                </Box>
                <Box sx={{ display: 'flex', alignItems: 'center', gap: 0.5 }}>
                  <PeopleIcon fontSize="small" color="action" />
                  <Typography variant="body2" color="text.secondary">
                    {item.students} students
                  </Typography>
                </Box>
              </Box>
              <Typography variant="body1" paragraph>
                {item.description}
              </Typography>
              <Box sx={{ display: 'flex', gap: 1, flexWrap: 'wrap', mb: 2 }}>
                {item.tags.map((tag, tagIndex) => (
                  <Chip
                    key={tagIndex}
                    label={tag}
                    size="small"
                    sx={{
                      backgroundColor: 'rgba(33, 150, 243, 0.1)',
                      color: '#2196F3',
                      '&:hover': {
                        backgroundColor: 'rgba(33, 150, 243, 0.2)',
                      },
                    }}
                  />
                ))}
              </Box>
              <Link
                href={item.syllabus}
                target="_blank"
                rel="noopener noreferrer"
                sx={{
                  display: 'flex',
                  alignItems: 'center',
                  gap: 0.5,
                  color: '#2196F3',
                  textDecoration: 'none',
                  '&:hover': {
                    textDecoration: 'underline',
                  },
                }}
              >
                <SchoolIcon fontSize="small" />
                View Syllabus
              </Link>
            </CardContent>
          </Card>
        ))}
      </Box>
    </Box>
  );
};

export default Teaching;
