import React, { ReactElement, useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import {
  AppBar,
  Toolbar,
  Typography,
  Container,
  Box,
  Button,
  Fab,
  useScrollTrigger,
  Slide,
  Drawer,
  IconButton,
  Avatar,
  List,
  ListItem,
  ListItemButton,
  ListItemIcon,
  ListItemText,
  Divider,
  useTheme,
  Tooltip,
} from '@mui/material';
import EmailIcon from '@mui/icons-material/Email';
import MenuIcon from '@mui/icons-material/Menu';
import HomeIcon from '@mui/icons-material/Home';
import SchoolIcon from '@mui/icons-material/School';
import WorkIcon from '@mui/icons-material/Work';
import ArticleIcon from '@mui/icons-material/Article';
import ClassIcon from '@mui/icons-material/Class';
import PersonIcon from '@mui/icons-material/Person';
import ChevronLeftIcon from '@mui/icons-material/ChevronLeft';
import ChevronRightIcon from '@mui/icons-material/ChevronRight';
import VolunteerActivismIcon from '@mui/icons-material/VolunteerActivism';
import PaletteIcon from '@mui/icons-material/Palette';
import LinkedInIcon from '@mui/icons-material/LinkedIn';
import DownloadIcon from '@mui/icons-material/Download';
import DescriptionIcon from '@mui/icons-material/Description';
import SchoolIcon2 from '@mui/icons-material/School';

interface Props {
  children: React.ReactNode;
}

function HideOnScroll(props: { children: ReactElement }) {
  const trigger = useScrollTrigger();
  return (
    <Slide appear={false} direction="down" in={!trigger}>
      {props.children}
    </Slide>
  );
}

const Layout: React.FC<Props> = ({ children }) => {
  const navigate = useNavigate();
  const location = useLocation();
  const [drawerOpen, setDrawerOpen] = useState(false);
  const [isCollapsed, setIsCollapsed] = useState(false);
  const theme = useTheme();

  const navItems = [
    { 
      label: 'Home', 
      path: '/', 
      icon: <HomeIcon />,
      color: '#2196F3', // Blue
    },
    { 
      label: 'Education', 
      path: '/education', 
      icon: <SchoolIcon />,
      color: '#4CAF50', // Green
    },
    { 
      label: 'Professional', 
      path: '/professional', 
      icon: <WorkIcon />,
      color: '#FF9800', // Orange
    },
    { 
      label: 'Publications', 
      path: '/publications', 
      icon: <ArticleIcon />,
      color: '#9C27B0', // Purple
    },
    { 
      label: 'Teaching', 
      path: '/teaching', 
      icon: <ClassIcon />,
      color: '#E91E63', // Pink
    },
    { 
      label: 'Non-Profit', 
      path: '/non-profit', 
      icon: <VolunteerActivismIcon />,
      color: '#00BCD4', // Cyan
    },
    { 
      label: 'Artworks', 
      path: '/artworks', 
      icon: <PaletteIcon />,
      color: '#FF5722', // Deep Orange
    },
    { 
      label: 'About', 
      path: '/about', 
      icon: <PersonIcon />,
      color: '#607D8B', // Blue Grey
    },
  ];

  const currentPage = navItems.find(item => item.path === location.pathname) || navItems[0];
  const currentColor = currentPage.color;

  const drawerWidth = isCollapsed ? 80 : 240;

  const handleDrawerToggle = () => {
    setIsCollapsed(!isCollapsed);
  };

  const actionButtons = [
    {
      icon: <LinkedInIcon />,
      tooltip: 'LinkedIn Profile',
      href: 'https://linkedin.com/in/your-profile',
      color: '#0077B5',
    },
    {
      icon: <DownloadIcon />,
      tooltip: 'Download CV',
      href: '/path-to-your-cv.pdf',
      color: '#4CAF50',
    },
    {
      icon: <DescriptionIcon />,
      tooltip: 'Download Resume',
      href: '/path-to-your-resume.pdf',
      color: '#FF9800',
    },
    {
      icon: <SchoolIcon2 />,
      tooltip: 'Google Scholar',
      href: 'https://scholar.google.com/citations?user=your-id',
      color: '#4285F4',
    },
  ];

  return (
    <Box sx={{ display: 'flex' }}>
      <AppBar 
        position="fixed" 
        elevation={0}
        sx={{ 
          width: { sm: `calc(100% - ${drawerWidth}px)` },
          ml: { sm: `${drawerWidth}px` },
          transition: 'width 0.2s, margin-left 0.2s',
          background: 'linear-gradient(45deg, #2196F3 30%, #21CBF3 90%)',
          color: 'white',
        }}
      >
        <Toolbar>
          <IconButton
            color="inherit"
            aria-label="open drawer"
            edge="start"
            onClick={() => setDrawerOpen(true)}
            sx={{ mr: 2, display: { sm: 'none' } }}
          >
            <MenuIcon />
          </IconButton>
          <Typography variant="h6" component="div" sx={{ flexGrow: 1, fontWeight: 600 }}>
            Your Name
          </Typography>
          <Box sx={{ display: 'flex', gap: 1 }}>
            {actionButtons.map((button, index) => (
              <Tooltip key={index} title={button.tooltip}>
                <IconButton
                  href={button.href}
                  target="_blank"
                  rel="noopener noreferrer"
                  sx={{
                    width: 40,
                    height: 40,
                    backgroundColor: 'rgba(255, 255, 255, 0.1)',
                    color: 'white',
                    '&:hover': {
                      backgroundColor: 'rgba(255, 255, 255, 0.2)',
                    },
                  }}
                >
                  {button.icon}
                </IconButton>
              </Tooltip>
            ))}
          </Box>
        </Toolbar>
      </AppBar>

      <Box
        component="nav"
        sx={{ width: { sm: drawerWidth }, flexShrink: { sm: 0 } }}
      >
        <Drawer
          variant="temporary"
          open={drawerOpen}
          onClose={() => setDrawerOpen(false)}
          ModalProps={{
            keepMounted: true,
          }}
          sx={{
            display: { xs: 'block', sm: 'none' },
            '& .MuiDrawer-paper': { 
              boxSizing: 'border-box', 
              width: drawerWidth,
              background: 'linear-gradient(180deg, #f5f5f5 0%, #ffffff 100%)',
            },
          }}
        >
          <Box sx={{ 
            p: 2, 
            display: 'flex', 
            flexDirection: 'column', 
            alignItems: 'center',
            background: 'linear-gradient(45deg, #2196F3 30%, #21CBF3 90%)',
            color: 'white',
          }}>
            <Avatar
              src="/path-to-your-photo.jpg"
              alt="Your Name"
              sx={{ 
                width: 100, 
                height: 100, 
                mb: 2,
                border: '3px solid white',
                boxShadow: '0 4px 8px rgba(0,0,0,0.1)',
              }}
            />
            <Typography variant="h6" gutterBottom sx={{ fontWeight: 600 }}>
              Your Name
            </Typography>
          </Box>
          <Divider />
          <List>
            {navItems.map((item) => (
              <ListItem key={item.path} disablePadding>
                <ListItemButton
                  selected={location.pathname === item.path}
                  onClick={() => {
                    navigate(item.path);
                    setDrawerOpen(false);
                  }}
                  sx={{
                    '&.Mui-selected': {
                      backgroundColor: `${item.color}15`,
                      '&:hover': {
                        backgroundColor: `${item.color}25`,
                      },
                    },
                  }}
                >
                  <ListItemIcon 
                    sx={{ 
                      color: location.pathname === item.path ? item.color : 'inherit',
                      minWidth: 40,
                      display: 'flex',
                      justifyContent: 'center',
                    }}
                  >
                    <Box
                      sx={{
                        width: 40,
                        height: 40,
                        borderRadius: '50%',
                        display: 'flex',
                        alignItems: 'center',
                        justifyContent: 'center',
                        backgroundColor: location.pathname === item.path ? `${item.color}15` : 'transparent',
                        transition: 'all 0.2s',
                        '&:hover': {
                          backgroundColor: `${item.color}25`,
                        },
                      }}
                    >
                      {item.icon}
                    </Box>
                  </ListItemIcon>
                  <ListItemText 
                    primary={item.label} 
                    primaryTypographyProps={{
                      fontWeight: location.pathname === item.path ? 600 : 400,
                      color: location.pathname === item.path ? item.color : 'inherit',
                    }}
                  />
                </ListItemButton>
              </ListItem>
            ))}
          </List>
        </Drawer>
        <Drawer
          variant="permanent"
          sx={{
            display: { xs: 'none', sm: 'block' },
            '& .MuiDrawer-paper': { 
              boxSizing: 'border-box', 
              width: drawerWidth,
              transition: 'width 0.2s',
              overflowX: 'hidden',
              background: 'linear-gradient(180deg, #f5f5f5 0%, #ffffff 100%)',
              borderRight: '1px solid rgba(0, 0, 0, 0.12)',
            },
          }}
          open
        >
          <Toolbar />
          <Box sx={{ 
            p: 2, 
            display: 'flex', 
            flexDirection: 'column', 
            alignItems: 'center',
            background: 'linear-gradient(45deg, #2196F3 30%, #21CBF3 90%)',
            color: 'white',
          }}>
            <Avatar
              src="/path-to-your-photo.jpg"
              alt="Your Name"
              sx={{ 
                width: isCollapsed ? 60 : 100, 
                height: isCollapsed ? 60 : 100, 
                mb: 2,
                transition: 'width 0.2s, height 0.2s',
                border: '3px solid white',
                boxShadow: '0 4px 8px rgba(0,0,0,0.1)',
              }}
            />
            {!isCollapsed && (
              <Typography variant="h6" gutterBottom sx={{ fontWeight: 600 }}>
                Your Name
              </Typography>
            )}
          </Box>
          <Divider />
          <List>
            {navItems.map((item) => (
              <ListItem key={item.path} disablePadding>
                <ListItemButton
                  selected={location.pathname === item.path}
                  onClick={() => navigate(item.path)}
                  sx={{
                    minHeight: 48,
                    justifyContent: isCollapsed ? 'center' : 'initial',
                    px: 2.5,
                    '&.Mui-selected': {
                      backgroundColor: `${item.color}15`,
                      '&:hover': {
                        backgroundColor: `${item.color}25`,
                      },
                    },
                  }}
                >
                  <ListItemIcon
                    sx={{
                      minWidth: 0,
                      mr: isCollapsed ? 'auto' : 3,
                      justifyContent: 'center',
                      color: location.pathname === item.path ? item.color : 'inherit',
                    }}
                  >
                    <Box
                      sx={{
                        width: 40,
                        height: 40,
                        borderRadius: '50%',
                        display: 'flex',
                        alignItems: 'center',
                        justifyContent: 'center',
                        backgroundColor: location.pathname === item.path ? `${item.color}15` : 'transparent',
                        transition: 'all 0.2s',
                        '&:hover': {
                          backgroundColor: `${item.color}25`,
                        },
                      }}
                    >
                      {item.icon}
                    </Box>
                  </ListItemIcon>
                  {!isCollapsed && (
                    <ListItemText 
                      primary={item.label}
                      primaryTypographyProps={{
                        fontWeight: location.pathname === item.path ? 600 : 400,
                        color: location.pathname === item.path ? item.color : 'inherit',
                      }}
                    />
                  )}
                </ListItemButton>
              </ListItem>
            ))}
          </List>
          <Box sx={{ flexGrow: 1 }} />
          <Divider />
          <IconButton 
            onClick={handleDrawerToggle} 
            sx={{ 
              m: 1,
              color: '#2196F3',
              '&:hover': {
                backgroundColor: 'rgba(33, 150, 243, 0.1)',
              },
            }}
          >
            {isCollapsed ? <ChevronRightIcon /> : <ChevronLeftIcon />}
          </IconButton>
        </Drawer>
      </Box>

      <Box
        component="main"
        sx={{
          flexGrow: 1,
          p: 3,
          width: { sm: `calc(100% - ${drawerWidth}px)` },
          transition: 'width 0.2s, margin-left 0.2s',
          background: '#fafafa',
          minHeight: '100vh',
        }}
      >
        <Toolbar />
        <Container sx={{ py: 4 }}>
          {children}
        </Container>
      </Box>

      <Fab
        color="primary"
        aria-label="contact"
        sx={{ 
          position: 'fixed', 
          bottom: 16, 
          right: 16,
          background: `linear-gradient(45deg, ${currentColor} 30%, ${currentColor}99 90%)`,
          '&:hover': {
            background: `linear-gradient(45deg, ${currentColor}99 30%, ${currentColor} 90%)`,
          },
        }}
        href="mailto:your.email@example.com"
      >
        <EmailIcon />
      </Fab>
    </Box>
  );
};

export default Layout;
