import React from 'react';
import {
  Box,
  Typography,
  Card,
  CardContent,
  Chip,
  Link,
} from '@mui/material';
import SchoolIcon from '@mui/icons-material/School';
import PeopleIcon from '@mui/icons-material/People';
import CalendarTodayIcon from '@mui/icons-material/CalendarToday';

const Teaching = () => {
  const themeColor = '#E91E63'; // Pink

  const teachingItems = [
    {
      title: 'Introduction to Computer Science',
      semester: 'Fall 2023',
      institution: 'Tulane University',
      description: 'An introductory course covering fundamental concepts in computer science, programming basics, and problem-solving techniques.',
      tags: ['Undergraduate', 'Core Course'],
    },
    {
      title: 'Data Structures and Algorithms',
      semester: 'Spring 2023',
      institution: 'Tulane University',
      description: 'Advanced course focusing on essential data structures, algorithm design, and analysis of computational complexity.',
      tags: ['Undergraduate', 'Advanced'],
    },
    {
      title: 'Software Engineering',
      semester: 'Fall 2022',
      institution: 'Tulane University',
      description: 'Comprehensive course on software development methodologies, design patterns, and best practices in modern software engineering.',
      tags: ['Undergraduate', 'Project-Based'],
    },
  ];

  return (
    <Box>
      <Typography 
        variant="h4" 
        gutterBottom 
        sx={{ 
          fontWeight: 600,
          mb: 4,
        }}
      >
        Teaching
      </Typography>
      <Box sx={{ display: 'flex', flexDirection: 'column', gap: 3 }}>
        {teachingItems.map((item, index) => (
          <Card 
            key={index}
            sx={{ 
              transition: 'transform 0.2s, box-shadow 0.2s',
              '&:hover': {
                transform: 'translateY(-4px)',
                boxShadow: '0 8px 16px rgba(0,0,0,0.1)',
              },
            }}
          >
            <CardContent>
              <Typography variant="h5" component="div" gutterBottom sx={{ fontWeight: 600 }}>
                {item.title}
              </Typography>
              <Box sx={{ display: 'flex', gap: 2, mb: 2, flexWrap: 'wrap' }}>
                <Box sx={{ display: 'flex', alignItems: 'center', gap: 0.5 }}>
                  <SchoolIcon sx={{ color: themeColor, fontSize: 20 }} />
                  <Typography variant="subtitle1" color="text.secondary">
                    {item.courseCode}
                  </Typography>
                </Box>
                <Box sx={{ display: 'flex', alignItems: 'center', gap: 0.5 }}>
                  <CalendarTodayIcon sx={{ color: themeColor, fontSize: 20 }} />
                  <Typography variant="subtitle1" color="text.secondary">
                    {item.semester}
                  </Typography>
                </Box>
                <Box sx={{ display: 'flex', alignItems: 'center', gap: 0.5 }}>
                  <PeopleIcon sx={{ color: themeColor, fontSize: 20 }} />
                  <Typography variant="subtitle1" color="text.secondary">
                    {item.students} Students
                  </Typography>
                </Box>
              </Box>
              <Typography variant="body1" paragraph>
                {item.description}
              </Typography>
              <Box sx={{ display: 'flex', gap: 1, flexWrap: 'wrap', mb: 2 }}>
                {item.tags.map((tag, tagIndex) => (
                  <Chip 
                    key={tagIndex} 
                    label={tag} 
                    size="small"
                    sx={{ 
                      backgroundColor: `${themeColor}15`,
                      color: themeColor,
                      '&:hover': {
                        backgroundColor: `${themeColor}25`,
                      },
                    }}
                  />
                ))}
              </Box>
            </CardContent>
          </Card>
        ))}
      </Box>
    </Box>
  );
};

export default Teaching;
