import React from 'react';
import {
  Box,
  Typography,
  Card,
  CardContent,
  CardMedia,
  Chip,
  Link,
} from '@mui/material';
import OpenInNewIcon from '@mui/icons-material/OpenInNew';

const Artworks: React.FC = () => {
  const artworkItems = [
    {
      title: 'Digital Dreams',
      medium: 'Digital Art',
      year: '2023',
      description: 'A series of digital artworks exploring the intersection of technology and human emotion. Created using generative AI and digital painting techniques.',
      exhibition: 'Virtual Gallery Exhibition',
      tags: ['Digital Art', 'AI Art', 'Generative'],
      link: 'https://example.com/digital-dreams',
      image: '/path-to-artwork-image1.jpg',
    },
    {
      title: 'Code Canvas',
      medium: 'Interactive Installation',
      year: '2022',
      description: 'An interactive art installation that visualizes code as flowing patterns of light and color. Visitors can interact with the installation through a custom interface.',
      exhibition: 'Tech Art Festival',
      tags: ['Interactive', 'Installation', 'Code Art'],
      link: 'https://example.com/code-canvas',
      image: '/path-to-artwork-image2.jpg',
    },
    {
      title: 'Data Portraits',
      medium: 'Data Visualization',
      year: '2021',
      description: 'A collection of data-driven portraits that visualize personal data in artistic ways. Each portrait represents a unique dataset transformed into visual art.',
      exhibition: 'Data Art Exhibition',
      tags: ['Data Art', 'Visualization', 'Portraits'],
      link: 'https://example.com/data-portraits',
      image: '/path-to-artwork-image3.jpg',
    },
  ];

  return (
    <Box>
      <Typography variant="h4" component="h1" gutterBottom>
        Artworks
      </Typography>
      <Box sx={{ display: 'flex', flexDirection: 'column', gap: 3 }}>
        {artworkItems.map((item, index) => (
          <Card 
            key={index}
            sx={{ 
              display: 'flex',
              flexDirection: { xs: 'column', md: 'row' },
              height: '100%',
              transition: 'transform 0.2s, box-shadow 0.2s',
              '&:hover': {
                transform: 'translateY(-4px)',
                boxShadow: '0 8px 16px rgba(0,0,0,0.1)',
              },
            }}
          >
            <CardMedia
              component="img"
              sx={{
                width: { xs: '100%', md: 240 },
                height: { xs: 200, md: 'auto' },
                objectFit: 'cover',
              }}
              image={item.image}
              alt={`${item.title} - ${item.medium}`}
            />
            <Box sx={{ display: 'flex', flexDirection: 'column', flexGrow: 1 }}>
              <CardContent sx={{ flex: '1 0 auto' }}>
                <Typography variant="h5" component="div" gutterBottom>
                  {item.title}
                </Typography>
                <Typography variant="h6" color="primary" gutterBottom>
                  {item.medium}
                </Typography>
                <Typography variant="subtitle1" color="text.secondary" gutterBottom>
                  {item.year} â€¢ {item.exhibition}
                </Typography>
                <Typography variant="body1" paragraph>
                  {item.description}
                </Typography>
                <Box sx={{ display: 'flex', gap: 1, flexWrap: 'wrap', mb: 2 }}>
                  {item.tags.map((tag, tagIndex) => (
                    <Chip
                      key={tagIndex}
                      label={tag}
                      size="small"
                      sx={{
                        backgroundColor: 'rgba(33, 150, 243, 0.1)',
                        color: '#2196F3',
                        '&:hover': {
                          backgroundColor: 'rgba(33, 150, 243, 0.2)',
                        },
                      }}
                    />
                  ))}
                </Box>
                <Link
                  href={item.link}
                  target="_blank"
                  rel="noopener noreferrer"
                  sx={{
                    display: 'flex',
                    alignItems: 'center',
                    gap: 0.5,
                    color: '#2196F3',
                    textDecoration: 'none',
                    '&:hover': {
                      textDecoration: 'underline',
                    },
                  }}
                >
                  View Artwork <OpenInNewIcon fontSize="small" />
                </Link>
              </CardContent>
            </Box>
          </Card>
        ))}
      </Box>
    </Box>
  );
};

export default Artworks;
