import React from 'react';
import {
  Box,
  Typography,
  Card,
  CardContent,
  CardMedia,
  Chip,
} from '@mui/material';

const Professional: React.FC = () => {
  const professionalItems = [
    {
      title: 'Senior Software Engineer',
      company: 'Tech Company Inc.',
      period: '2020 - Present',
      description: 'Leading development of cloud-native applications using modern technologies. Mentoring junior developers and implementing best practices.',
      image: '/path-to-company-image.jpg',
      location: 'City, Country',
      tags: ['Cloud', 'Leadership', 'Mentoring'],
    },
    {
      title: 'Software Engineer',
      company: 'Previous Company',
      period: '2018 - 2020',
      description: 'Developed and maintained enterprise applications. Collaborated with cross-functional teams to deliver high-quality software solutions.',
      image: '/path-to-company-image.jpg',
      location: 'City, Country',
      tags: ['Enterprise', 'Full Stack', 'Agile'],
    },
    {
      title: 'Junior Developer',
      company: 'Startup Company',
      period: '2016 - 2018',
      description: 'Contributed to the development of web applications and participated in code reviews and team meetings.',
      image: '/path-to-company-image.jpg',
      location: 'City, Country',
      tags: ['Web Development', 'Team Collaboration'],
    },
  ];

  const themeColor = '#FF9800'; // Orange

  return (
    <Box>
      <Typography 
        variant="h4" 
        gutterBottom 
        sx={{ 
          fontWeight: 600,
          color: themeColor,
          mb: 4,
        }}
      >
        Professional Experience
      </Typography>
      <Box sx={{ display: 'flex', flexDirection: 'column', gap: 3 }}>
        {professionalItems.map((item, index) => (
          <Card 
            key={index}
            sx={{ 
              display: 'flex',
              flexDirection: { xs: 'column', md: 'row' },
              transition: 'transform 0.2s, box-shadow 0.2s',
              '&:hover': {
                transform: 'translateY(-4px)',
                boxShadow: '0 8px 16px rgba(0,0,0,0.1)',
              },
            }}
          >
            <CardMedia
              component="img"
              sx={{ 
                width: { xs: '100%', md: 200 },
                height: { xs: 200, md: 'auto' },
                objectFit: 'cover',
              }}
              image={item.image}
              alt={item.title}
            />
            <CardContent sx={{ flex: 1 }}>
              <Typography variant="h5" component="div" gutterBottom sx={{ fontWeight: 600, color: themeColor }}>
                {item.title}
              </Typography>
              <Typography variant="subtitle1" color="text.secondary" gutterBottom>
                {item.company}
              </Typography>
              <Typography variant="body2" color="text.secondary" gutterBottom>
                {item.period} â€¢ {item.location}
              </Typography>
              <Typography variant="body1" paragraph>
                {item.description}
              </Typography>
              <Box sx={{ display: 'flex', gap: 1, flexWrap: 'wrap' }}>
                {item.tags.map((tag, tagIndex) => (
                  <Chip 
                    key={tagIndex} 
                    label={tag} 
                    size="small"
                    sx={{ 
                      backgroundColor: `${themeColor}15`,
                      color: themeColor,
                      '&:hover': {
                        backgroundColor: `${themeColor}25`,
                      },
                    }}
                  />
                ))}
              </Box>
            </CardContent>
          </Card>
        ))}
      </Box>
    </Box>
  );
};

export default Professional;
