import React from 'react';
import {
  Box,
  Typography,
  Card,
  CardContent,
  Chip,
  Link,
  CardMedia,
} from '@mui/material';
import SchoolIcon from '@mui/icons-material/School';
import PeopleIcon from '@mui/icons-material/People';
import CalendarTodayIcon from '@mui/icons-material/CalendarToday';
import OpenInNewIcon from '@mui/icons-material/OpenInNew';

const Teaching: React.FC = () => {
  const themeColor = '#E91E63'; // Pink

  const teachingItems = [
    {
      title: 'Introduction to Computer Science',
      courseCode: 'CS101',
      semester: 'Fall 2023',
      description: 'An introductory course covering fundamental concepts in computer science and programming.',
      students: '120',
      tags: ['Programming', 'Algorithms', 'Data Structures'],
      syllabusLink: 'https://example.com/syllabus1',
      image: '/path-to-course-image1.jpg',
    },
    {
      title: 'Advanced Machine Learning',
      courseCode: 'CS401',
      semester: 'Spring 2023',
      description: 'Advanced topics in machine learning, including deep learning and neural networks.',
      students: '45',
      tags: ['Machine Learning', 'Deep Learning', 'AI'],
      syllabusLink: 'https://example.com/syllabus2',
      image: '/path-to-course-image2.jpg',
    },
    {
      title: 'Software Engineering',
      courseCode: 'CS301',
      semester: 'Fall 2022',
      description: 'Software development methodologies, tools, and best practices.',
      students: '75',
      tags: ['Software Development', 'Agile', 'DevOps'],
      syllabusLink: 'https://example.com/syllabus3',
      image: '/path-to-course-image3.jpg',
    },
  ];

  return (
    <Box>
      <Typography 
        variant="h4" 
        gutterBottom 
        sx={{ 
          fontWeight: 600,
          mb: 4,
        }}
      >
        Teaching Experience
      </Typography>
      <Box sx={{ display: 'flex', flexDirection: 'column', gap: 3 }}>
        {teachingItems.map((item, index) => (
          <Card 
            key={index}
            sx={{ 
              display: 'flex',
              flexDirection: { xs: 'column', md: 'row' },
              transition: 'transform 0.2s, box-shadow 0.2s',
              '&:hover': {
                transform: 'translateY(-4px)',
                boxShadow: '0 8px 16px rgba(0,0,0,0.1)',
              },
            }}
          >
            <CardMedia
              component="img"
              sx={{ 
                width: { xs: '100%', md: 200 },
                height: { xs: 200, md: 'auto' },
                objectFit: 'cover',
              }}
              image={item.image}
              alt={item.title}
            />
            <CardContent sx={{ flex: 1 }}>
              <Typography variant="h5" component="div" gutterBottom sx={{ fontWeight: 600 }}>
                {item.title}
              </Typography>
              <Typography variant="subtitle1" color="text.secondary" gutterBottom>
                {item.courseCode}
              </Typography>
              <Typography variant="body2" color="text.secondary" gutterBottom>
                {item.semester} â€¢ {item.students} students
              </Typography>
              <Typography variant="body1" paragraph>
                {item.description}
              </Typography>
              <Box sx={{ display: 'flex', gap: 1, flexWrap: 'wrap', mb: 2 }}>
                {item.tags.map((tag, tagIndex) => (
                  <Chip 
                    key={tagIndex} 
                    label={tag} 
                    size="small"
                    sx={{ 
                      backgroundColor: `${themeColor}15`,
                      color: themeColor,
                      '&:hover': {
                        backgroundColor: `${themeColor}25`,
                      },
                    }}
                  />
                ))}
              </Box>
              <Link 
                href={item.syllabusLink} 
                target="_blank" 
                rel="noopener noreferrer"
                sx={{ 
                  color: themeColor,
                  textDecoration: 'none',
                  display: 'flex',
                  alignItems: 'center',
                  gap: 0.5,
                  '&:hover': {
                    textDecoration: 'underline',
                  },
                }}
              >
                View Syllabus
                <OpenInNewIcon sx={{ fontSize: 16 }} />
              </Link>
            </CardContent>
          </Card>
        ))}
      </Box>
    </Box>
  );
};

export default Teaching;
