import React from 'react';
import {
  Box,
  Typography,
  Card,
  CardContent,
  CardMedia,
  Chip,
} from '@mui/material';

const NonProfit: React.FC = () => {
  const nonProfitItems = [
    {
      title: 'Board Member',
      organization: 'Tech for Good Foundation',
      period: '2022 - Present',
      description: 'Leading initiatives to promote technology education in underserved communities. Organizing workshops and mentoring programs for young students interested in STEM fields.',
      location: 'San Francisco, CA',
      tags: ['Education', 'Mentoring', 'STEM Outreach'],
      image: '/path-to-nonprofit-image1.jpg',
    },
    {
      title: 'Volunteer Coordinator',
      organization: 'Code for Community',
      period: '2021 - 2022',
      description: 'Coordinated volunteer efforts for a non-profit organization focused on providing free coding education to underprivileged youth. Managed a team of 20+ volunteers and organized weekly coding workshops.',
      location: 'Remote',
      tags: ['Volunteer Management', 'Education', 'Community Outreach'],
      image: '/path-to-nonprofit-image2.jpg',
    },
    {
      title: 'Project Lead',
      organization: 'Digital Literacy Initiative',
      period: '2020 - 2021',
      description: 'Led a project to provide digital literacy training to senior citizens. Developed curriculum and training materials, and organized community workshops.',
      location: 'Local Community Centers',
      tags: ['Digital Literacy', 'Community Service', 'Education'],
      image: '/path-to-nonprofit-image3.jpg',
    },
  ];

  return (
    <Box>
      <Typography variant="h4" component="h1" gutterBottom>
        Non-Profit Work
      </Typography>
      <Box sx={{ display: 'flex', flexDirection: 'column', gap: 3 }}>
        {nonProfitItems.map((item, index) => (
          <Card 
            key={index}
            sx={{ 
              display: 'flex',
              flexDirection: { xs: 'column', md: 'row' },
              height: '100%',
              transition: 'transform 0.2s, box-shadow 0.2s',
              '&:hover': {
                transform: 'translateY(-4px)',
                boxShadow: '0 8px 16px rgba(0,0,0,0.1)',
              },
            }}
          >
            <CardMedia
              component="img"
              sx={{
                width: { xs: '100%', md: 240 },
                height: { xs: 200, md: 'auto' },
                objectFit: 'cover',
              }}
              image={item.image}
              alt={`${item.organization} - ${item.title}`}
            />
            <Box sx={{ display: 'flex', flexDirection: 'column', flexGrow: 1 }}>
              <CardContent sx={{ flex: '1 0 auto' }}>
                <Typography variant="h5" component="div" gutterBottom>
                  {item.title}
                </Typography>
                <Typography variant="h6" color="primary" gutterBottom>
                  {item.organization}
                </Typography>
                <Typography variant="subtitle1" color="text.secondary" gutterBottom>
                  {item.period} • {item.location}
                </Typography>
                <Typography variant="body1" paragraph>
                  {item.description}
                </Typography>
                <Box sx={{ display: 'flex', gap: 1, flexWrap: 'wrap' }}>
                  {item.tags.map((tag, tagIndex) => (
                    <Chip
                      key={tagIndex}
                      label={tag}
                      size="small"
                      sx={{
                        backgroundColor: 'rgba(33, 150, 243, 0.1)',
                        color: '#2196F3',
                        '&:hover': {
                          backgroundColor: 'rgba(33, 150, 243, 0.2)',
                        },
                      }}
                    />
                  ))}
                </Box>
              </CardContent>
            </Box>
          </Card>
        ))}
      </Box>
    </Box>
  );
};

export default NonProfit;
